exec("""\nMAXKEYSIZE=26 \ndef getMode():\n  while True:\n    print('Do you wish to encrypt or decrypt a message?');mode=input().lower()\n    if mode in 'encrypt e decrypt d brute b'.split():return mode\n    else:print('Enter either "encrypt" or "e" or "decrypt" or "d" or "brute" or "b".')\ndef getMessage():print('Enter your message:');return input()\ndef getKey():\n  key=0\n  while True:\n    print('Enter the key number (1-%s)'%(MAXKEYSIZE));key=int(input())\n    if key>=1 and key<=MAXKEYSIZE:return key\ndef getTranslatedMessage(mode,message,key):\n  if mode[0]=='d':key=-key\n  translated=''\n  for symbol in message:\n    if symbol.isalpha():\n      num=ord(symbol);num+=key\n      if symbol.isupper() and num>ord('Z'):num-=26\n      elif symbol.isupper() and num<ord('A'):num+=26\n      elif symbol.islower() and num>ord('z'):num-=26\n      elif symbol.islower() and num<ord('a'):num+=26\n      translated+=chr(num)\n    else:translated+=symbol\n  return translated\nmode=getMode();message=getMessage()\nif mode[0]!='b':key=getKey()\nprint('Your translated text is:')\nif mode[0]!='b':print(getTranslatedMessage(mode,message,key))\nelse:\n  for key in range(1,MAXKEYSIZE+1):print(key,getTranslatedMessage('decrypt',message,key))\n""")